        $(document).ready(function() {
            // Add recipient
            $('#addRecipient').click(function() {
                const newRow = `
                    <div class="recipient-row">
                        <input type="email" class="recipient-email" placeholder="recipient@example.com" required>
                        <button type="button" class="remove-btn">×</button>
                    </div>
                `;
                $('#recipientsList').append(newRow);
                updateRemoveButtons();
            });

            // Remove recipient
            $(document).on('click', '.remove-btn', function() {
                $(this).closest('.recipient-row').remove();
                updateRemoveButtons();
            });

            // Update remove buttons visibility
            function updateRemoveButtons() {
                const rows = $('.recipient-row');
                if (rows.length === 1) {
                    rows.find('.remove-btn').hide();
                } else {
                    $('.remove-btn').show();
                }
            }

            // Populate subcategories based on category
            $('#category').change(function() {
                const category = $(this).val();
                const subcategoryDropdown = $('#subcategory');
                subcategoryDropdown.empty();
                subcategoryDropdown.append('<option value="">Select a Subcategory</option>');
                if (templates[category]) {
                    Object.keys(templates[category]).forEach(sub => {
                        subcategoryDropdown.append(`<option value="${sub}">${sub}</option>`);
                    });
                }
                $('#message').val(''); // Clear message on category change
            });

            // Update message based on subcategory
            $('#subcategory').change(function() {
                const category = $('#category').val();
        const subcategory = $(this).val();
        let messageTemplate = templates[category]?.[subcategory] || '';

        // Replace {{customerName}} with recipient email prefix
        const recipientEmail = $('.recipient-email').first().val();
        if (recipientEmail) {
            const emailPrefix = recipientEmail.split('@')[0];
            messageTemplate = messageTemplate.replace(/{{customerName}}/g, emailPrefix);
        }

        $('#message').val(messageTemplate);

            });

            // Handle form submission
            $('#emailForm').submit(function(e) {
                e.preventDefault();

                const recipients = [];
                $('.recipient-email').each(function() {
                    const email = $(this).val().trim();
                    if (email) {
                        recipients.push({ email: email });
                    }
                });

                const data = {
                    recipients: recipients,
                    subject: $('#subject').val(),
                    text: $('#message').val()
                };

                // Send email
                $.ajax({
                    url: '/api/send',
                    type: 'POST',
                    contentType: 'application/json',
                    data: JSON.stringify(data),
                    success: function(response) {
                        $('#statusMessage').removeClass('error').addClass('success')
                            .html(`Success! Sent to ${response.successful.length} recipients.`);
                        if (response.failed.length) {
                            $('#statusMessage').append(`<br>Failed to send to ${response.failed.length} recipients.`);
                        }
                    },
                    error: function(xhr) {
                        const message = xhr.responseJSON?.message || 'Error sending emails. Please try again.';
                        $('#statusMessage').removeClass('success').addClass('error')
                            .text(message);
                    }
                });
            });
        });
